ern":{"layers":{}},"textBased":{"layers":{}}},"internalReferences":[],"adHocDataViews":{}}},"enhancements":{}},"title":"Active Players Over Time"},{"type":"visualization","gridData":{"x":24,"y":0,"w":24,"h":15,"i":"84472c36-4965-4fda-90d1-45d1525e26c1"},"panelIndex":"84472c36-4965-4fda-90d1-45d1525e26c1","embeddableConfig":{"savedVis":{"id":"","title":"","description":"","type":"vega","params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "autosize": { "type": "fit", "contains": "padding" },
  "height": 260,
  "padding": 10,
  "title": "Weekly Active vs Returning Players (platform_login)",

  "data": [
    {
      "name": "events",
      "url": {
        "index": "platform-events-*",
        "body": {
          "size": 10000,
          "_source": ["@timestamp", "playerId", "eventType"],
          "query": {
            "bool": {
              "filter": [
                { "range": { "@timestamp": { "%timefilter%": true } } },
                { "term": { "eventType": "platform_login" } }
              ]
            }
          }
        }
      },
      "format": { "property": "hits.hits" },
      "transform": [
        {
          "type": "formula",
          "as": "ts",
          "expr": "toDate(datum._source['@timestamp'])"
        },
        {
          "type": "formula",
          "as": "playerId",
          "expr": "toString(datum._source.playerId)"
        },
        { "type": "filter", "expr": "datum.ts != null && datum.playerId != null" },

        {
          "type": "formula",
          "as": "utcMidnight",
          "expr": "datetime(utcyear(datum.ts), utcmonth(datum.ts), utcdate(datum.ts))"
        },
        {
          "type": "formula",
          "as": "weekStart",
          "expr": "datetime(utcyear(datum.utcMidnight), utcmonth(datum.utcMidnight), utcdate(datum.utcMidnight) - ((utcday(datum.utcMidnight) + 6) % 7))"
        }
      ]
    },

    {
      "name": "playerWeeks",
      "source": "events",
      "transform": [
        { "type": "aggregate", "groupby": ["weekStart", "playerId"] }
      ]
    },

    {
      "name": "firstWeekPerPlayer",
      "source": "playerWeeks",
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["playerId"],
          "ops": ["min"],
          "fields": ["weekStart"],
          "as": ["firstWeek"]
        }
      ]
    },

    {
      "name": "weekly",
      "source": "playerWeeks",
      "transform": [
        {
          "type": "lookup",
          "from": "firstWeekPerPlayer",
          "key": "playerId",
          "fields": ["playerId"],
          "values": ["firstWeek"],
          "as": ["firstWeek"]
        },
        {
          "type": "formula",
          "as": "firstWeekScalar",
          "expr": "isArray(datum.firstWeek) ? datum.firstWeek[0] : datum.firstWeek"
        },
        {
          "type": "formula",
          "as": "returningFlag",
          "expr": "datum.weekStart > datum.firstWeekScalar ? 1 : 0"
        },
        {
          "type": "aggregate",
          "groupby": ["weekStart"],
          "ops": ["distinct", "sum"],
          "fields": ["playerId", "returningFlag"],
          "as": ["uniquePlayers", "returningPlayers"]
        },
        { "type": "collect", "sort": { "field": "weekStart" } }
      ]
    }
  ],

  "scales": [
    { "name": "x", "type": "time", "domain": { "data": "weekly", "field": "weekStart" }, "range": "width" },
    {
      "name": "y",
      "type": "linear",
      "domain": { "data": "weekly", "fields": ["uniquePlayers", "returningPlayers"] },
      "nice": true,
      "zero": true,
      "range": "height"
    }
  ],

  "axes": [
    { "orient": "bottom", "scale": "x", "title": "Week (Mon start, UTC)" },
    { "orient": "left", "scale": "y", "title": "Players" }
  ],

  "marks": [
    {
      "type": "line",
      "from": { "data": "weekly" },
      "encode": {
        "update": {
          "x": { "scale": "x", "field": "weekStart" },
          "y": { "scale": "y", "field": "uniquePlayers" },
          "strokeWidth": { "value": 2 }
        }
      }
    },
    {
      "type": "line",
      "from": { "data": "weekly" },
      "encode": {
        "update": {
          "x": { "scale": "x", "field": "weekStart" },
          "y": { "scale": "y", "field": "returningPlayers" },
          "strokeDash": { "value": [6, 4] },
          "strokeWidth": { "value": 2 }
        }
      }
    }
  ]
